import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"
import p7 from './p7.png'

export default class Prob10 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.state = {
      corr : "작은 별",
      answer:"",
      isCorrect:false
    };
  }

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">“좀비들이 냄새를 맡고 다시 몰려올 거야 어서 다른 장소로 가자.” 
          <br></br><br></br>지성은 지내는 장소를 자주 옮겨 본 듯 하다.
          <br></br><br></br>둘은 보호복을 입고 실험도구를 빠르게 챙겨 생명공학연구센터 밖을 나선다.
          <br></br><br></br>“어디로 가야 할까?”
          <br></br><br></br>“청암은 어떨까? 그곳에는 우리 연구에 필요한 자료가 많을 거야.”
          <br></br><br></br>둘은 청암 방향으로 뛰어가기 시작했다.
          <br></br><br></br>하지만 청암의 입구는 이미 좀비로 가득했고 둘은 108계단 방향으로 재빨리 도망친다.
          <br></br><br></br>“아마 무은재 기념관에는 아직 좀비들이 도착하지 못했을 거야 그쪽으로 가자.” 
          <br></br><br></br>무은재 기념관에 도착한 승훈과 지성은 암호로 잠긴 무은재 기념관의 정문을 연다.</div></div>
        <hr className="hline"></hr>
        
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image"
              src={p7}
              onClick={this.handleShowDialog}
              className='popup'
              alt="no image"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 10]
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Prob11" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
