import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"

import p21 from './../image/p21.png'

export default class Prob21 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.handleShowDialog = this.handleShowDialog.bind(this);
    this.state = {
      corr : "RIGHT",
      answer:"",
      isCorrect:false,
      isOpen:false
    };
  }
  handleShowDialog = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">승훈은 어지러운 정신을 차리고 옆에 떨어진 유리조각을 집는다.
          <br></br><br></br>밧줄을 현수 몰래 끊기 시작했고, 현수는 지성을 좀비에게 끌고 가고 있다.
          <br></br><br></br>“잠깐, 얘기 좀 해! 우리는 백신을 만들고 있어. 우리가 사라진다면 인류는 정말 끝이야!”
          <br></br><br></br>“백신...? 백신이 정말로 개발될 수 있으면 진작에 만들어졌겠지! 내가 관찰한 좀비는 기형 생물체 그 자체야. 백신이 나올 수 없는 존재들이라고!”, 현수가 소리친다.
          <br></br><br></br>“그러니까... 너희도 희망을 버려.”
          <br></br><br></br>“아니야. 내 백신은 거의 만들어졌어. 희망이 있다고!”, 대화를 통해 승훈은 최대한 시간을 끈다.
          </div></div>
        <hr className="hline"></hr>
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image"
              src={p21}
              onClick={this.handleShowDialog}
              className='popup'
              alt="no image"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 21]<br></br><br></br>
          <img src = {p21} onClick={this.handleShowDialog} className='imageShow'></img>          
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Prob22" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
