import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./maze.css"
import p37 from './../image/p37-1.png'

export default class Prob37_1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.handleShowDialog = this.handleShowDialog.bind(this);
    this.state = {
      corr : "RIGHT",
      answer:"",
      isCorrect:false,
      isOpen:false
    };
  }
  handleShowDialog = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">문을 닫자마자 안쪽에서 엄청난 힘으로 문을 미는 게 느껴졌고,
          <br></br><br></br>사람은 승훈의 손을 잡고 뛰어간다.
          <br></br><br></br>승훈은 손을 뿌리치며 말한다.
          <br></br><br></br>“잠깐만요. 어디 가시는 거예요!?”
          <br></br><br></br>그 사람의 얼굴은 겁에 질린 표정이었고,
          <br></br><br></br>승훈은 민간인 대피소로 데려간다.
          </div></div>
        <hr className="hline"></hr>
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image"
              src={p37}
              onClick={this.handleShowDialog}
              className='popup'
              alt="no image"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 37]
          <br></br><br></br>포준위가 열심히 회의중이다. 현재 발언 순서를 정하고 있다.
          <br></br><br></br>
          <img alt='small' src = {p37} onClick={this.handleShowDialog} className='imageShow'></img>          
          <br></br><br></br><br></br>재완 = 0
          <br></br><br></br>지성 *  현수= ?
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Ocp86ezGn2lr99ILsj3RUhtkH4Bsx6a3JKqmzb/7OgI/+YrnMiUVayxsnO3b/Bb1RT6PpJ/BDl2Wo4hVRqRu9A==" : "/7sjrqEkyg6q88MlGzvRObRHsgYNXIHdO50NWwM97jwZofLsKEwECxAhPJEz/t0cTPlPGxuLSrkhBKdoKQ9SiRw==")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
