import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"
import dataService from "../services/user.service";

export default class Prob29_1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.submit = this.submit.bind(this);
    this.state = {
      answer:"",
      link : "/",
    };
  }

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }
  submit() {
    this.setState({
      answer: ''
    });
    dataService.getAns('',this.state.answer).then(response=>{
      this.setState({
        link: response.data
      })
    }).catch(function (error) {
      console.log(error);
    });;
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom"> 청암 밖 굶주린 좀비들 또한 지성과 승훈을 보고 쫓아가기 시작한다.
          <br></br><br></br>도착한 곳은 화학관이었고, 좀비가 없다고 판단한 지성과 승훈은 화학관 안에 숨어 숨을 돌린다.
          <br></br><br></br>“우리 논문은 찾았어?”, 승훈이 물었다.
          <br></br><br></br>지성은 자랑스럽게 상자 하나를 꺼내 들었고, 상자에는 [포스텍 의학 논문 - 바이러스]라 쓰여있었다.
          <br></br><br></br>하지만 상자는 숫자로 된 자물쇠가 잠겨있고, 옆에는 의미심장한 문구가 쓰여있다.
          </div></div>
        <hr className="hline"></hr>
        <div className="story">
          <div className="marginbottom">[문제 29]
          <br></br><br></br>z = (0,0,0)
          <br></br><br></br>(8,1,0)(2,2,0)(3,1,0)(4,2,0) = ?
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/VFHHgvHN/0M3u0pHKA0QQYhwtJl/qCx0ih90/p/eKnVQStg/la0JWV5tSLrM1ZqXGT6dMXVtqtrpP4cCYOSvHA==" : "/YC46YlnW0TZ6zbtbTXe3vHuYhnEOATek77iOvwM1okVv1onIajICuJ9cHZXZ7UxRF3hBo3EzBAHRRuBfVTFefw==")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
