import React, { Component } from "react";
import UserDataService from "../services/user.service";
import { Link} from "react-router-dom";
import "./maze.css"

export default class Prob1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.state = {
      answer:"",
      isCorrect:false
    };
  }
  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }
  render() {
    return (
      <div>
        <table>
          <tr>
            <td>
              <div className="story">
                <div className="marginbottom">스토리</div>
              </div>
            </td>
          </tr>
          <tr>
            <td>
              <hr className="hline"></hr>
            </td>
          </tr>
          <tr className="story">
            <div className="marginbottom">문제</div>               
          </tr>
          <tr>
            <td>
              <form>
                <input
                  type="text"
                  className="form-control"
                  id="answer"
                  required
                  value={this.state.answer}
                  onChange={this.onChangeAnswer}
                  name="answer"
                />
              </form>
            </td>
            <td>              
              <Link to="/Home" className={'btn'}>이동</Link>
            </td>       
          </tr>
        </table>        
      </div>
    );
  }
}
