import React, { Component } from "react";
import UserDataService from "../services/user.service";
import { Link } from "react-router-dom";
import "./maze.css"

export default class UsersList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchAnswer = this.onChangeSearchAnswer.bind(this);

    this.state = {
    };
  }

  onChangeSearchAnswer(e) {
    const searchAnswer = e.target.value;

    this.setState({
      searchAnswer: searchAnswer
    });
  }
  render() {
    const { searchAnswer, users, currentUser, currentIndex } = this.state;

    return (

      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by Answer"
              value={searchAnswer}
              onChange={this.onChangeSearchAnswer}
            />
            <div className="input-group-append">
              <button
                className="btn"
                type="button"
                onClick={this.searchAnswer}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Users List</h4>

          <ul className="list-group">
            {users &&
              users.map((user, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveUser(user, index)}
                  key={index}
                >
                  {user.answer}
                </li>
              ))}
          </ul>

          <button
            className="btn"
            onClick={this.removeAllUsers}
          >
            Remove All
          </button>
        </div>
        <div className="col-md-6">
          {currentUser ? (
            <div>
              <h4>User</h4>
              <div>
                <label>
                  <strong>Answer:</strong>
                </label>{" "}
                {currentUser.answer}
              </div>
              <div>
                <label>
                  <strong>IP:</strong>
                </label>{" "}
                {currentUser.ip}
              </div>
              <div>
                <label>
                  <strong>ID:</strong>
                </label>{" "}
                {currentUser.id}
              </div>
              <div>
                <label>
                  <strong>TIMESTAMP:</strong>
                </label>{" "}
                {currentUser.time}
              </div>
              
              <Link
                to={"/users/" + currentUser.id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a User...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
