import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"

export default class Prob29_2 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.state = {
      corr : "작은 별",
      answer:"",
      isCorrect:false
    };
  }

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom"> 청암 밖 굶주린 좀비들 또한 지성과 승훈을 보고 쫓아가기 시작한다.
          <br></br><br></br>도착한 곳은 화학관이었고, 좀비가 없다고 판단한 지성과 승훈은 화학관 안에 숨어 숨을 돌린다.
          <br></br><br></br>“우리 논문은 찾았어?”, 승훈이 물었다.
          <br></br><br></br>지성은 자랑스럽게 상자 하나를 꺼내 들었고, 상자에는 [포스텍 의학 논문 - 바이러스]라 쓰여있었다.
          <br></br><br></br>하지만 상자는 숫자로 된 자물쇠가 잠겨있고, 옆에는 의미심장한 문구가 쓰여있다.
          </div></div>
        <hr className="hline"></hr>
        <div className="story">
          <div className="marginbottom">[문제 29]
          <br></br><br></br>나
          <br></br><br></br>멀리
          <br></br><br></br>바느질 두번
          <br></br><br></br>토르 헤위에르달이 탄 배
          <br></br><br></br>차
          <br></br><br></br>암사슴 두마리
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/VFHHgvHN/0M3u0pHKA0QQYhwtJl/qCx0ih90/p/eKnVQStg/la0JWV5tSLrM1ZqXGT6dMXVtqtrpP4cCYOSvHA==" : "/IouJYkICcGX1Jp2/c9OEGIdusGbKH+//qclU3P0fY5io27M1yO/t0WbYjp4fAENuOTO7Ta6CWxMxhjaSyybL9Q==")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
