import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"
import p23 from './../image/p23.png'

export default class Prob23 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.handleShowDialog = this.handleShowDialog.bind(this);
    this.state = {
      corr : "RIGHT",
      answer:"",
      isCorrect:false,
      isOpen:false
    };
  }
  handleShowDialog = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };
  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">학생회관에서 나온 후 지성이 정신 차릴 때까지 기다린다.
          <br></br><br></br>“지성아, 정신 차려. 우리 이렇게 쉬고 있을 시간이 없어!”
          <br></br><br></br>“으.. 머리 아파, 무슨 일 있었어?”
          <br></br><br></br>“자세한 것들은 나중에 설명해줄게. 얼른 청암으로 가자.”
          <br></br><br></br>지성과 승훈은 빠르게 폭풍의 언덕을 가로질러 가기로 했다.
          <br></br><br></br>학생회관에서 내려와 폭풍의 언덕을 뛰어간다.
          <br></br><br></br>그때 승훈의 발에 무언가 걸리며 넘어진다.
          </div></div>
        <hr className="hline"></hr>
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image"
              src={p23}
              onClick={this.handleShowDialog}
              className='popup'
              alt="no image"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 23]
          <br></br><br></br>맥주를 마시며 시를 읽는다.<br></br><br></br>
          <img src = {p23} onClick={this.handleShowDialog} className='imageShow'></img>
          
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Prob21" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
