import React, { Component } from "react";
import UserDataService from "../services/user.service";
import { Link} from "react-router-dom";
import "./maze.css"

export default class Prob1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.validate = this.validate.bind(this);
    this.state = {
      corr : "13",
      answer:"",
      isCorrect:false
    };
  }
  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }
  validate() {
    if(this.answer === this.corr) {
      this.isCorrect = true;
    }
    return this.isCorrect;
  }
  render() {
    return (
      <div>
        <table>
          <tr>
              <div className="story">
                <div className="marginbottom">스토리</div>
              </div>
          </tr>
          <tr>
              <hr className="hline"></hr>
          </tr>
          <tr className="story">
            <div className="marginbottom">문제</div>               
          </tr>
          </table>
            <div className="flex-container">
              <form>
                <input
                  className="answer"
                  placeholder="정답입력"
                  type="text"
                  id="answer"
                  required
                  value={this.state.answer}
                  onChange={this.onChangeAnswer + this.validate}
                  name="answer"
                />
              </form>
              <Link to={(this.isCorrect ? "/Home" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
            </div>
      </div>
    );
  }
}
