import React, { Component } from "react";
import { Link } from "react-router-dom";
import "./maze.css"
import p32 from './../image/p32-1.png'

export default class Prob32_1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.handleShowDialog = this.handleShowDialog.bind(this);
    this.state = {
      corr : "작은 별",
      answer:"",
      isCorrect:false,
      isOpen:false
    };
  }
  handleShowDialog = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">가속기연구소 사람들은 승훈을 들여보내고, 지금껏 승훈의 백신 개발 과정을 듣는다.
          <br></br><br></br>백신 개발에 실패했다는 사실에 사람들은 절망한다.
          <br></br><br></br>그때, 승훈을 따라오던 좀비 하나가 대피소의 문을 두드린다.
          <br></br><br></br>‘쿵…..쿵….’
          <br></br><br></br>승훈은 곧바로 출입문 옆에 보이는 비상벨을 누른다.
          <br></br><br></br>가속기연구소 안 사람들은 순식간에 공황에 빠진다.
          <br></br><br></br>그때 누군가 소리친다.
          </div></div>
        <hr className="hline"></hr>
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image popup"
              src={p32}
              onClick={this.handleShowDialog}
              alt="no"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 32]
          <br></br><br></br>
          <img alt='small' src = {p32} onClick={this.handleShowDialog} className='imageShow'></img>
          </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/V8CnHqaHhJglXgZzsbvXgVCEqBF0gtb6wzJ98v9z3J81Xc7ZhrLiXVDadI0RbNvVyRyRlOSyhu+Z42GlWGakxw==" : "/bqxL2Ct5W1lKxF5XZ8asAIY5Gx4d8w6WCLFO7klTy490yYVT6QoC6U4JLucrrcuS686rkhx/KUxRVb/nPZGgug==")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
