import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"

export default class Home extends Component {  
  constructor(props) {
    super(props);    
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeNum = this.onChangeNum.bind(this);
    this.onChangeTel = this.onChangeTel.bind(this);
    this.state = {
        num:1,
        name:"",
        num:"",
        tel:""
    };
  }
  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }
  onChangeNum(e) {
    this.setState({
      num: e.target.value,
    });
  }
  onChangeTel(e) {
    this.setState({
      tel: e.target.value,
    });
  }
  render() {    
    return (
        <div>
        <div className="select-title">
            미궁의 {this.state.num} 번째 클리어를 축하드립니다!
        </div>
        <div className="flex-container">
            <div>
                이름
            </div>
          <form>
            <input
              className="answer"
              placeholder="이름"
              type="text"
              required
              value={this.state.name}
              onChange={this.onChangeName}
            />
          </form>
          
        </div><div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답입력"
              type="text"
              required
              value={this.state.num}
              onChange={this.onChangeNum}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Select" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
        </div><div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답입력"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Select" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
        </div>
        <div>
          <Link to="/Prob1"> <button className="btn">이동</button></Link>
        </div>   
      </div>
    );
  }
}
