import React, { Component } from "react";
import UserDataService from "../services/user.service";
import { Link} from "react-router-dom";
import "./maze.css"

export default class Prob1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.state = {
      corr : "ab",
      answer:"",
      isCorrect:false
    };
  }

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
    console.log(this.answer);
  }

  render() {
    return (
      <div>
              <div className="story">
                <div className="marginbottom">스토리</div>
              </div><hr className="hline"></hr>
          <div className="story">
            <div className="marginbottom">문제</div>        
            </div> 
          <div className="flex-container">
            <form>
              <input
                className="answer"
                placeholder="정답입력"
                type="text"
                id="answer"
                required
                value={this.state.answer}
                onChange={this.onChangeAnswer}
                name="answer"
              />
            </form>
            <Link to={((this.answer === this.corr) ? "/Home" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
          </div>
      </div>
    );
  }
}
