import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"
import p28 from './../image/p28-2.png'

export default class Prob28_2 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.handleShowDialog = this.handleShowDialog.bind(this);
    this.state = {
      corr : "RIGHT",
      answer:"",
      isCorrect:false,
      isOpen:false
    };
  }
  handleShowDialog = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom"> 하지만 몇 분 지나지 않아 아래서 유리가 깨지는 소리에 잠을 깬다.
          <br></br><br></br>좀비들이 1층 문을 깨고 들어온 것이었다.
          <br></br><br></br>“승훈아! 계단으로 뛰어!!”
          <br></br><br></br>지성은 승훈 쪽으로 달려왔고, 함께 계단을 오르기 시작한다.
          <br></br><br></br>승훈과 지성은 계단을 타고 5층으로 올라간다.
          <br></br><br></br>5층에 올라와 문을 향해 달린다.
          <br></br><br></br>회전문을 통해 밖으로 나온 후 죽을힘을 다해 목적지 없이 뛴다.
          </div></div>
        <hr className="hline"></hr>
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image"
              src={p28}
              onClick={this.handleShowDialog}
              className='popup'
              alt="no image"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 28]
          <br></br><br></br>
          <img src = {p28} onClick={this.handleShowDialog} className='imageShow'></img>
          
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/Prob29-2" : "/Prob1")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
