import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"

import p37 from './../image/p37.png'

export default class Prob37_1 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.handleShowDialog = this.handleShowDialog.bind(this);
    this.state = {
      corr : "RIGHT",
      answer:"",
      isCorrect:false,
      isOpen:false
    };
  }
  handleShowDialog = () => {
    this.setState({ isOpen: !this.state.isOpen });
  };

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">바로 연구소의 생산라인을 가동시켜 백신 대량 생산에 들어간다.
          <br></br><br></br>승훈과 지성은 연구소 안 민간인 대피소에 들어간다.
          <br></br><br></br>가속기연구소엔 사람들이 서로를 도우며 모여있었고, 식량과 물은 마치 이 상황을 대비했다는 듯이 충분했다.
          <br></br><br></br>“지성아, 수고했어.”
          <br></br><br></br>“그러게, 난 우리가 성공할 줄 몰랐어...”
          <br></br><br></br>“우리 이제 안심하고 푹 쉬자.”
          </div></div>
        <hr className="hline"></hr>
        {this.state.isOpen && (
          <dialog
            className={"popupDialog"}
            open
            onClick={this.handleShowDialog}
          >
            <img
              className="image"
              src={p37}
              onClick={this.handleShowDialog}
              className='popup'
              alt="no image"
            />
          </dialog>
        )}
        <div className="story">
          <div className="marginbottom">[문제 37]
          <br></br><br></br>
          <img src = {p37} onClick={this.handleShowDialog} className='imageShow'></img>
        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 단어"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/End-3" : "/Prob37-3")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
