import React, { Component } from "react";
import { Link} from "react-router-dom";
import "./maze.css"

export default class Prob12 extends Component {
  constructor(props) {
    super(props);
    this.onChangeAnswer = this.onChangeAnswer.bind(this);
    this.state = {
      corr : "11",
      answer:"",
      isCorrect:false
    };
  }

  onChangeAnswer(e) {
    this.setState({
      answer: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <div className="story">
          <div className="marginbottom">“지성아, 이게 무슨 소리지?”
          <br></br><br></br>“벌써 좀비가 들어온 건가...?
          <br></br><br></br>밖에서 무언가가 아우성거리는 소리가 들리기 시작한다.
          <br></br><br></br>소리는 점점 선명해지고 가까워지는 것 같다.
          <br></br><br></br>“일단 옥상으로 가자. 빨리 짐 챙겨!”</div></div>
        <hr className="hline"></hr>
        <div className="story">
          <div className="marginbottom">[문제 12]
          <br></br><br></br>(6, 5, 1) * (3, 2, 3) = 111
          <br></br><br></br>(5, 4, 2) * (3, 2, 1) = ?

        </div>        
        </div> 
        <div className="flex-container">
          <form>
            <input
              className="answer"
              placeholder="정답 형식: 숫자"
              type="text"
              required
              value={this.state.answer}
              onChange={this.onChangeAnswer}
            />
          </form>
          <Link to={((this.state.answer === this.state.corr) ? "/dKScaY29PBLjawsodEfYM/dPOTf/Ey6/9wVLqhhiPDWnBbsYuC4qwDhLUSfblwFuY2CfcSvJDjUGz76pdZn0bw==" : "/FDdgKwda7bi5Ypf44yvW4Hj/jFN7yNI0/xDEPKnOvg5nV3zE4Ixx5F3Jn1R7BHJ93CmMgOPyvZISMMRqJPWULA==")} ><button className={'btn'}>이동</button></Link>
        </div>
      </div>
    );
  }
}
